// Particle system
const particleContainer = document.createElement('div');
particleContainer.style.position = 'relative';
particleContainer.style.width = '100%';
particleContainer.style.height = '300px';
particleContainer.style.overflow = 'hidden';
particleContainer.style.backgroundColor = '#1a1a2e';

const particles = [];

function createParticle() {
    const particle = document.createElement('div');
    particle.style.position = 'absolute';
    particle.style.width = '4px';
    particle.style.height = '4px';
    particle.style.borderRadius = '50%';
    particle.style.backgroundColor = '#4ecdc4';
    particle.style.left = Math.random() * 400 + 'px';
    particle.style.top = '300px';

    const speed = Math.random() * 2 + 1;
    const angle = Math.random() * Math.PI * 2;
    const vx = Math.cos(angle) * speed;
    const vy = Math.sin(angle) * speed - 2;

    particle.vx = vx;
    particle.vy = vy;
    particle.life = 100;

    particleContainer.appendChild(particle);
    particles.push(particle);
}

function animateParticles() {
    particles.forEach((particle, index) => {
        particle.life--;
        particle.style.left = (parseFloat(particle.style.left) + particle.vx) + 'px';
        particle.style.top = (parseFloat(particle.style.top) + particle.vy) + 'px';
        particle.vy += 0.1; // gravity

        // Fade out
        particle.style.opacity = particle.life / 100;

        if (particle.life <= 0) {
            particle.remove();
            particles.splice(index, 1);
        }
    });
}

// Create particles periodically
const particleInterval = setInterval(createParticle, 50);
const animationInterval = setInterval(animateParticles, 16);

// Stop after 15 seconds
setTimeout(() => {
    clearInterval(particleInterval);
    clearInterval(animationInterval);
    particles.forEach(particle => particle.remove());
}, 15000);

document.getElementById('output').appendChild(particleContainer);

// Interactive drawing canvas
const canvas = document.createElement('canvas');
canvas.width = 400;
canvas.height = 300;
canvas.style.border = '2px solid #4ecdc4';
canvas.style.borderRadius = '10px';
canvas.style.cursor = 'crosshair';
canvas.style.display = 'block';
canvas.style.margin = '20px auto';

const ctx = canvas.getContext('2d');
ctx.lineWidth = 3;
ctx.lineCap = 'round';

let isDrawing = false;
let lastX = 0;
let lastY = 0;

const colors = ['#ff6b6b', '#4ecdc4', '#45b7d1', '#96ceb4', '#feca57', '#ff9ff3'];
let currentColor = colors[0];

// Color picker
const colorPicker = document.createElement('div');
colorPicker.style.textAlign = 'center';
colorPicker.style.marginBottom = '10px';

colors.forEach(color => {
    const colorBtn = document.createElement('button');
    colorBtn.style.width = '30px';
    colorBtn.style.height = '30px';
    colorBtn.style.borderRadius = '50%';
    colorBtn.style.border = '3px solid #fff';
    colorBtn.style.backgroundColor = color;
    colorBtn.style.margin = '0 5px';
    colorBtn.style.cursor = 'pointer';

    if (color === currentColor) {
        colorBtn.style.border = '3px solid #333';
    }

    colorBtn.addEventListener('click', () => {
        currentColor = color;
        ctx.strokeStyle = color;
        colorPicker.querySelectorAll('button').forEach(btn => {
            btn.style.border = '3px solid #fff';
        });
        colorBtn.style.border = '3px solid #333';
    });

    colorPicker.appendChild(colorBtn);
});

// Drawing functions
canvas.addEventListener('mousedown', (e) => {
    isDrawing = true;
    lastX = e.offsetX;
    lastY = e.offsetY;
});

canvas.addEventListener('mousemove', (e) => {
    if (!isDrawing) return;

    ctx.strokeStyle = currentColor;
    ctx.beginPath();
    ctx.moveTo(lastX, lastY);
    ctx.lineTo(e.offsetX, e.offsetY);
    ctx.stroke();

    lastX = e.offsetX;
    lastY = e.offsetY;
});

canvas.addEventListener('mouseup', () => {
    isDrawing = false;
});

// Clear button
const clearBtn = document.createElement('button');
clearBtn.textContent = 'Clear Canvas';
clearBtn.style.padding = '8px 16px';
